import React, { useState, useEffect } from 'react';
import { Container, Form } from 'react-bootstrap';
import { sha256 } from 'js-sha256';
import { NumberField, ReadOnlyTextField, TextAreaField } from '../Fields';
import { MineButton, mine } from '../MineButton';
import MineSettings from '../MineSettings';

// Parse the mining settings.
const difficulty = MineSettings.difficulty;
const pattern = MineSettings.pattern;

// Define component globals.                                                      
const GREEN = '#50C878';                                                          
const RED = '#FF0000';

function SingleBlock(props) {
    const [blockNumber, setBlockNumber] = useState(props.block.blockNumber);
    const [nonce, setNonce] = useState(props.block.nonce);
    const [data, setData] = useState(props.block.data);
    const [color, setColor] = useState(GREEN);
    const [isSigned, setIsSigned] = useState(true);

    /*
    // Update the hash.
    useEffect(() => {
        setHash(String(sha256(blockNumber + nonce + data + prevHash)));
    }, [blockNumber, nonce, data, prevHash]);
    */

    // Set isSigned.
    useEffect(() => {
        setIsSigned(props.block.hash.substr(0, difficulty) === pattern ? true : false);
    });

    // Set color.
    useEffect(() => {
        setColor(isSigned ? GREEN : RED);
    });

    // Notify the caller of the changes. 
    useEffect(() => {
        let newBlock = {...props.block};
        newBlock.blockNumber = blockNumber;
        newBlock.nonce = nonce;
        newBlock.data = data;
        props.callback(newBlock);
    }, [blockNumber, nonce, data]);

    return (
        <Container>
            <Container
                style={{
                    'width':350,
                    'height':500,
                    'background-color':color
                }}
            >
                <Form>
                    <br/>
                    <NumberField
                        label={'Block Number:'}
                        value={blockNumber}
                        callback={(blockNumber) => setBlockNumber(String(blockNumber))}
                    />
                    <NumberField
                        label={'Nonce:'}
                        value={nonce}
                        callback={(nonce) => setNonce(String(nonce))}
                    />
                    <TextAreaField
                        label={'Data:'}
                        callback={(data) => setData(String(data))}
                    />
                    <ReadOnlyTextField
                        label={'Previous Hash:'}
                        value={props.block.prevHash}
                    />
                    <ReadOnlyTextField
                        label={'Hash:'}
                        value={props.block.hash}
                    />
                    <br/>
                    <MineButton
                        blockNumber={blockNumber}
                        data={data}
                        prevHash={props.block.prevHash}
                        callback={(nonce) => setNonce(nonce)}
                    />
                </Form>
            </Container>
        </Container>
    );
}

export default SingleBlock;
