import { useState, useEffect } from 'react';
import { Button } from 'react-bootstrap';
import { sha256 } from 'js-sha256';
import MineSettings from './MineSettings';

// Parse the mining settings.
const difficulty = MineSettings.difficulty;
const maxNonce = MineSettings.maximumNonce;
const pattern = MineSettings.pattern;

/*
function* nonceIter(blockNumber, data, prevHash) {
    const hash = nonce => sha256(blockNumber + nonce + data + prevHash);
    const isSigned = hash => hash.substr(0, difficulty) === pattern;
    let nonce = 0;
    while (!isSigned(hash(nonce))) {
        yield nonce;
        nonce++;
    }
}
*/

function* nonceIter(nonce = 0) {
    yield nonce;
    nonce++;
}

function hash(nonce) {
   return sha256(blockNumber + nonce + data + prevHash); 
}

function isSigned(hash) {
    return hash.substr(0, difficulty) === pattern;
}

function MineButton({blockNumber, data, prevHash, callback, block, setBlock}) {
    /*
    const mine = (nonce = 0) => {
        const hash = nonce => sha256(blockNumber + nonce + data + prevHash);
        const isSigned = hash => hash.substr(0, difficulty) === pattern;
        while (!isSigned(hash(nonce))) {
            nonce++;
        }
        return nonce;
    }   
    */

    /*
    const handleClick = () => {
        let nonce = 0;
        const interval = setInterval(() => {
            nonce = nonceIter(blockNumber, data, prevHash).next().value;
            if (nonce % 10 === 0) {
                setBlock({...block, nonce}); 
            }
        }, 1000);
        return () => clearInterval(interval);
    }
    */
    //const hash = nonce => sha256(blockNumber + nonce + data + prevHash);
    //const isSigned = hash => hash.substr(0, difficulty) === pattern;

    const setNonce = () => {
        let nonce = nonceIter().next().value;
        console.log("NONCE", nonce);
        if (isSigned(hash(nonce))) {
            setBlock({...block, nonce});
            return;
        } else {
            const timer = setTimeout(() => {
                setBlock({...block, nonce});
                setNonce();
            }, 1000);
        }
    }

    const handleClick = () => {
        setNonce()
    }

    return (
        <Button 
            onClick={() => handleClick()}
        >
            {'Mine!'}
        </Button>
    );
};

export { MineButton }
