import React, { useState, useEffect } from 'react';
import { Container, Form } from 'react-bootstrap';
import { sha256 } from 'js-sha256';
import { TitleField, BodyField, NumberField, ReadOnlyTextField, TextAreaField } from '../Fields';
import { MineButton } from '../MineButton';
import MineSettings from '../MineSettings';
import description from './description';
import { SwitchButton } from '../Switch';
import DefaultBlocks from '../Blockchain/DefaultBlocks';

// Parse the mining settings.
const difficulty = MineSettings.difficulty;
const pattern = MineSettings.pattern;

// Define component globals.
const GREEN = '#50C878';
const RED = '#FF0000';

const BLOCK_WIDTH = 350;
const BLOCK_HEIGHT = 450;

const defaultBlock = DefaultBlocks.block1;



function Block(props) {
    const [blockNumber, setBlockNumber] = useState(defaultBlock.blockNumber);
    const [nonce, setNonce] = useState(defaultBlock.nonce);
    const [data, setData] = useState(defaultBlock.data);

    const hash = () => sha256(sha256(blockNumber + nonce + data));
    const isSigned = () => hash().substr(0, difficulty) === pattern;

    return (
        <Container>
            <TitleField titleText={description.title} />
            <BodyField bodyText={description.body} />
            <Container 
                style={{
                    'width':BLOCK_WIDTH,
                    'height':BLOCK_HEIGHT,
                    'background-color':isSigned() ? GREEN : RED
                }}
            >
                <Form>
                    <br/>
                    <NumberField 
                        label={'Block Number:'} 
                        value={blockNumber} 
                        callback={(blockNumber) => setBlockNumber(blockNumber)}
                    />
                    <NumberField 
                        label={'Nonce:'} 
                        value={nonce} 
                        callback={(nonce) => setNonce(nonce)} 
                    />
                    <TextAreaField 
                        label={'Data:'} 
                        callback={(data) => setData(data)}
                    />
                    <ReadOnlyTextField 
                        label={'Hash:'}
                        value={hash()}
                    />
                    <br/>
                    <MineButton 
                        blockNumber={blockNumber} 
                        data={data} 
                        callback={(nonce) => setNonce(nonce)}
                    />
                    <SwitchButton
                    />
                </Form>
            </Container>
        </Container>
    );
}

export default Block;
