import React, { Component } from 'react';
import { Form } from 'react-bootstrap';

class DataField extends Component {
    onTrigger = (event) => {
        this.props.parentCallback(event.target);
        event.preventDefault();
    };

    render() {
        return (
            <Form.Group onChange={(event) => {
                this.onTrigger(event);
            }}>
                <Form.Label>
                    Data:
                </Form.Label>
                <Form.Control
                    as="textarea"
                    type="text"
                    placeholder=""
                    ref={(input) =>
                        console.log(input)
                    }
                    value = 'DATA FIELD TEXT'
                />
            </Form.Group>
        );
    };
}

export default DataField;
