import React, { Component } from 'react';
import { Container, Form, Button } from 'react-bootstrap';
import { sha256 } from 'js-sha256';
//import {render} from "react-dom";
import get from '../descriptionLoader';
import { NumberField, DataField, HashField, TextAreaField } from './Fields';
import MineButton from './Mine';

let difficulty = 4;        // number of zeros required at front of hash
let maximumNonce = 500000; // limit the nonce to this so we don't mine too long

class SingleBlock extends Component {
    constructor(props) {
        super(props);
        this.state = {
            pattern: '0'.repeat(difficulty),
            blockNumber: 1,
            nonce: 72608,
            data: "",
            hash: sha256("1" + "72608" + ""),
            //green
            color: "#50C878"
        };
        this.handleColorChange = this.handleColorChange.bind(this);
    }

    handleColorChange() {
        var col = this.state.color
        this.props.getColor(col)        
    }

    getHashData() {
        return this.state.blockNumber.toString() + this.state.nonce.toString() + this.state.data.toString();
    }

    render() {
        return (
                <Container fluid>
                    <Form>
                       <NumberField name={'Block Number:'} value={this.state.blockNumber} callback={(blockNumber) => this.setState({blockNumber})} />
                       <NumberField name={'Nonce:'} value={this.state.nonce} callback={(nonce) => this.setState({nonce})} />
                       <TextAreaField name={'Data:'} callback={(data) => this.setState({data})} />
                       <HashField data={getHashData()} />
                    </Form>
                    <MineButton pattern={this.state.pattern} blockNumber={this.state.blockNumber} data={this.state.data} callback={(nonce) => this.setState({nonce})}/>
                </Container>
        );
    }
}

export default SingleBlock;
