import React, { Component } from 'react';
import { Container, Form, Button } from 'react-bootstrap';
import { sha256 } from 'js-sha256';
import { NumberField, ReadOnlyTextField, HashField, TextAreaField } from './Fields';
import MineButton from './MineButton';
import get from '../descriptionLoader';

class SingleBlock extends Component {
    constructor(props) {
        super(props);
        this.state = {
            blockNumber: 1,
            nonce: 72608,
            data: "",
            hash: sha256("1" + "72608" + ""),
            color: "#50C878" // Green
        };
        this.handleColorChange = this.handleColorChange.bind(this);
    }

    handleColorChange() {
        var col = this.state.color
        this.props.getColor(col)        
    }

    getHashData() {
        return this.state.blockNumber.toString() + this.state.nonce.toString() + this.state.data.toString();
    }

    render() {
        return (
            <Container fluid>
                <Form>
                    <br/>
                    <NumberField 
                        label={'Block Number:'} 
                        value={this.state.blockNumber} 
                        callback={(blockNumber) => {
                            this.setState({blockNumber})
                            this.setState({hash: sha256(this.getHashData())})
                        }} 
                    />
                    <NumberField 
                        label={'Nonce:'} 
                        value={this.state.nonce} 
                        callback={(nonce) => {
                            this.setState({nonce})
                            this.setState({hash: sha256(this.getHashData())})
                        }}
                    />
                    <TextAreaField 
                        label={'Data:'} 
                        callback={(data) => {
                            this.setState({data})
                            this.setState({hash: sha256(this.getHashData())})
                        }}
                    />
                    <ReadOnlyTextField 
                        label={'Hash:'}
                        value={this.state.hash}
                    />
                </Form>
                <br/>
                <MineButton 
                    blockNumber={this.state.blockNumber} 
                    data={this.state.data} 
                    callback={(nonce) => this.setState({nonce})}
                />
                <p></p>
            </Container>
        );
    }
}

export default SingleBlock;
