import React, { useState, useEffect } from 'react';
import { Container, Form } from 'react-bootstrap';
import { sha256 } from 'js-sha256';
import { NumberField, ReadOnlyTextField, TextAreaField } from '../Fields';
import { MineButton, mine } from '../MineButton';
import MineSettings from '../MineSettings';

// Parse the mining settings.
const difficulty = MineSettings.difficulty;
const pattern = MineSettings.pattern;

// Define component globals.                                                      
const GREEN = '#50C878';                                                          
const RED = '#FF0000';

function SingleBlock(props) {
    const [block, setBlock] = useState(props.block);
    /*
    const [blockNumber, setBlockNumber] = useState(String(props.block.blockNumber));
    const [nonce, setNonce] = useState(String(props.block.nonce));
    const [data, setData] = useState(String(props.block.data));
    const [prevHash, setPrevHash] = useState(String(props.block.prevHash));
    const [hash, setHash] = useState(String(props.block.hash));
    */
    const [color, setColor] = useState(GREEN);
    const [isSigned, setIsSigned] = useState(true);

    useEffect(() => {
        setHash(String(sha256(block.blockNumber + block.nonce + block.data + block.prevHash)));
    }, [block.blockNumber, block.nonce, block.data, block.prevHash]);

    useEffect(() => {
        setIsSigned(block.hash.substr(0, difficulty) === pattern ? true : false);
    }, [block.hash]);

    useEffect(() => {
        setColor(isSigned ? GREEN : RED);
    }, [isSigned]);

    useEffect(() => {
        /*
        const response = {
            blockNumber, 
            nonce,
            data,
            prevHash, 
            hash
        };
        props.callback(response);
    }, [blockNumber, nonce, data, prevHash, hash]);
    */
        props.callback(block);
    }, [block]);

    return (
        <Container>
            <Container
                style={{
                    'width':350,
                    'height':500,
                    'background-color':color
                }}
            >
                <Form>
                    <br/>
                    <NumberField
                        label={'Block Number:'}
                        value={block.blockNumber}
                        //callback={(blockNumber) => setBlockNumber(String(blockNumber))}
                        callback={(blockNumber) => setBlock({...block, blockNumber})}
                    />
                    <NumberField
                        label={'Nonce:'}
                        value={block.nonce}
                        //callback={(nonce) => setNonce(String(nonce))}
                        callback={(nonce) => setBlock({...block, nonce})}
                    />
                    <TextAreaField
                        label={'Data:'}
                        //callback={(data) => setData(String(data))}
                        callback={(data) => setBlock({...block, data})}
                    />
                    <ReadOnlyTextField
                        label={'Prev:'}
                        value={props.block.prevHash}
                        //callback={(prevHash) => setPrevHash(String(prevHash))}
                        callback={(prevHash) => setBlock({...block, prevHash})}
                    />
                    <ReadOnlyTextField
                        label={'Hash:'}
                        value={block.hash}
                        //callback={(hash) => setHash(String(hash))}
                        callback={(hash) => setBlock({...block, hash})}
                    />
                    <br/>
                    <MineButton
                        blockNumber={block.blockNumber}
                        data={block.data}
                        prevHash={props.block.prevHash}
                        //callback={(nonce) => setNonce(String(nonce))}
                        callback={(nonce) => setBlock({...block, nonce})}
                    />
                </Form>
            </Container>
        </Container>
    );
}

export default SingleBlock;
