import { useState, useEffect } from 'react';
import { Button } from 'react-bootstrap';
import { sha256 } from 'js-sha256';
import MineSettings from './MineSettings';

// Parse the mining settings.
const difficulty = MineSettings.difficulty;
const maximumNonce = MineSettings.maximumNonce;
const pattern = MineSettings.pattern;

/*
function mine(blockNumber, data, prevHash="", block, setBlock) {
    let sequence = null;
    let hash = "";
    for (let nonce = 0; nonce < maximumNonce; nonce++) {
        hash = sha256(String(blockNumber + nonce + data + prevHash));
        if (hash.substr(0, difficulty) === pattern) {
            setBlock({...block, nonce});
            return;
        } else if (nonce % 10 === 0) {
            setTimeout(() => {
                setBlock({...block, nonce});
            }, nonce);
        }
    }
}
*/


function mine(blockNumber, data, prevHash="", block, setBlock) {
    function* nonceIter() {
        for (let nonce = 0; nonce < maximumNonce; nonce++) {
            yield nonce;
        }
    }

    function getHash(nonce) {
        return String(sha256(blockNumber + nonce + data + prevHash));
    }

    let nonce = nonceIter().next().value;
    let hash = getHash(nonce);

    while (hash.substr(0, difficulty) !== pattern) {
        setBlock({...block, nonce});
        nonce = nonceIter().next().value;
        hash = getHash(nonce);
    }
}



