function MineButton({blockNumber, data, prevHash="", block, setBlock}) {
    return (
        <Button 
            onClick={() => mine(blockNumber, data, prevHash, block, setBlock)}
        >
            {'Mine!'}
        </Button>
    );
};

export { MineButton, mine }
