import React, { useState, useEffect } from 'react';
import { Container, Form } from 'react-bootstrap';
import { sha256 } from 'js-sha256';
import { NumberField, ReadOnlyTextField, TextAreaField } from '../Fields';
import { MineButton, mine } from '../MineButton';
import MineSettings from '../MineSettings';

// Parse the mining settings.
const difficulty = MineSettings.difficulty;
const pattern = MineSettings.pattern;

// Define component globals.                                                      
const GREEN = '#50C878';                                                          
const RED = '#FF0000';

function SingleBlock(props) {
    //const [block, setBlock] = useState(props.block);
    const [color, setColor] = useState(GREEN);
    const [isSigned, setIsSigned] = useState(true);

    // Update the hash.
    useEffect(() => {
        setBlock({...block, hash:(String(sha256(block.blockNumber + block.nonce + block.data + block.prevHash)))});
    }, [block.blockNumber, block.nonce, block.data, block.prevHash]);

    // Set isSigned.
    useEffect(() => {
        setIsSigned(props.block.hash.substr(0, difficulty) === pattern ? true : false);
    }, [props.block.hash]);

    // Set color.
    useEffect(() => {
        setColor(isSigned ? GREEN : RED);
    }, [isSigned]);

    // Return the updated block to caller.
    useEffect(() => {
        props.callback(block);
    }, [block]);

    return (
        <Container>
            <Container
                style={{
                    'width':350,
                    'height':500,
                    'background-color':color
                }}
            >
                <Form>
                    <br/>
                    <NumberField
                        label={'Block Number:'}
                        value={props.block.blockNumber}
                        callback={(blockNumber) => props.setBlockNumber(blockNumber)}
                    />
                    <NumberField
                        label={'Nonce:'}
                        value={props.block.nonce}
                        callback={(nonce) => props.setNonce(nonce)}
                    />
                    <TextAreaField
                        label={'Data:'}
                        callback={(data) => props.setData(data)}
                    />
                    <ReadOnlyTextField
                        label={'Prev:'}
                        value={props.block.prevHash}
                        callback={(prevHash) => props.setPrevHash(prevHash)}
                    />
                    <ReadOnlyTextField
                        label={'Hash:'}
                        value={props.block.hash}
                        callback={(hash) => props.setHash(hash)}
                    />
                    <br/>
                    <MineButton
                        blockNumber={props.block.blockNumber}
                        data={props.block.data}
                        prevHash={props.block.prevHash}
                        callback={(nonce) => props.setNonce(nonce)}
                    />
                </Form>
            </Container>
        </Container>
    );
}

export default SingleBlock;
