import React, { useState, useEffect } from 'react';
import { Container, Form, Row } from 'react-bootstrap';
import { NumberField, ReadOnlyTextField, TextAreaField } from './Fields';
import { MineButton } from './MineButton';
import MineSettings from './MineSettings';
import { sha256 } from 'js-sha256';
import { SwitchButton } from './Switch';

// Parse the mining settings.
const difficulty = MineSettings.difficulty;
const pattern = MineSettings.pattern;

// Define component globals.                                                      
const GREEN = '#50C878';                                                          
const RED = '#FF0000';

function SingleBlock({block, setBlock}) {
    const {blockNumber, nonce, data, prevHash} = block;
    const [isSwitchOn, setIsSwitchOn] = useState(false);

    const hash = () => sha256(blockNumber + nonce + data + prevHash);
    const isSigned = () => hash().substr(0, difficulty) === pattern;
    // Set the hash.
    useEffect(() => {
        setBlock({...block, hash: String(sha256(blockNumber + nonce + data + prevHash))});
    }, [blockNumber, nonce, data, prevHash]);

    // Set isSigned.
    useEffect(() => {
        setIsSigned(hash.substr(0, difficulty) === pattern ? true : false);
    });

    return (
        <Container
            style={{
                'width':350,
                'height':525,
                'background-color':isSigned() ? GREEN : RED
            }}
        >
            <Form>
                <br/>
                <NumberField
                    label={'Block Number:'}
                    value={blockNumber}
                    callback={(blockNumber) => setBlock({...block, blockNumber})}
                />
                <NumberField
                    label={'Nonce:'}
                    value={nonce}
                    callback={(nonce) => setBlock({...block, nonce})}
                />
                <TextAreaField
                    label={'Data:'}
                    callback={(data) => setBlock({...block, data})}
                />
                <ReadOnlyTextField
                    label={'Previous Hash:'}
                    value={prevHash}
                />
                <ReadOnlyTextField
                    label={'Hash:'}
                    value={hash()}
                />
                <br/>
                <Row className="g-2">
                    <MineButton
                        blockNumber={blockNumber}
                        data={data}
                        prevHash={prevHash}
                        block={block}
                        setBlock={setBlock}
                        isSwitchOn = {isSwitchOn}
                    />
                    <SwitchButton
                        isSwitchOn = {isSwitchOn}
                        setIsSwitchOn={setIsSwitchOn}
                    />
                </Row>

            </Form>
        </Container>
    );
}

export default SingleBlock;
