import React, { useEffect } from 'react';
import { Container, Form } from 'react-bootstrap';
import { NumberField, ReadOnlyTextField, TextAreaField } from '../Fields';
import { MineButton } from '../MineButton';
import MineSettings from '../MineSettings';
import { sha256 } from 'js-sha256';

// Parse the mining settings.
const difficulty = MineSettings.difficulty;
const pattern = MineSettings.pattern;

// Define component globals.                                                      
const GREEN = '#50C878';                                                          
const RED = '#FF0000';


function SingleBlock({index, block, setBlocks}) {
    const {blockNumber, nonce, data, prevHash, hash} = block;

    // Update the hash.
    useEffect(() => {
        setBlocks({...block, hash: String(sha256(blockNumber + nonce + data + prevHash))});
    }, [blockNumber, nonce, data, prevHash]);

    return (
        <Container
            style={{
                'width':350,
                'height':500,
                'background-color':hash.substr(0, difficulty) === pattern ? GREEN : RED
            }}
        >
            <Form>
                <br/>
                <NumberField
                    label={'Block Number:'}
                    value={blockNumber}
                    callback={(blockNumber) => setBlocks({...block, blockNumber})}
                />
                <NumberField
                    label={'Nonce:'}
                    value={nonce}
                    callback={(nonce) => setBlocks({...block, nonce})}
                />
                <TextAreaField
                    label={'Data:'}
                    callback={(data) => setBlocks({...block, data})}
                />
                <ReadOnlyTextField
                    label={'Previous Hash:'}
                    value={prevHash}
                />
                <ReadOnlyTextField
                    label={'Hash:'}
                    value={hash}
                />
                <br/>
                <MineButton
                    blockNumber={blockNumber}
                    data={data}
                    prevHash={prevHash}
                    callback={(nonce) => setBlocks({...block, nonce})}
                />
            </Form>
        </Container>
    );
}

export default SingleBlock;
