import React, { useState, useEffect } from 'react';
import { Container } from 'react-bootstrap';
import SingleBlock from './SingleBlock';
import DefaultBlocks from './DefaultBlocks';

function Blockchain(props) {
    const [block1, setBlock1] = useState(DefaultBlocks.block1);
    const [block2, setBlock2] = useState(DefaultBlocks.block2);
    const [block3, setBlock3] = useState(DefaultBlocks.block3);

    // Update block2 prevHash to match block1 hash.
    useEffect(() => {
        setBlock2({
            blockNumber: block2.blockNumber,
            nonce: block2.nonce,
            data: block2.data,
            prevHash: block1.hash,
            hash: block2.hash
        });
    }, [block2.blockNumber, block2.nonce, block2.data, block2.hash, block1.hash]);

    // Update block3 prevHash to match block2 hash.
    useEffect(() => {
        setBlock3({...block3, prevHash: block2.hash});
    }, [block2]);

    return (
        <Container fluid>
            <div class = "row justify-content-around" >
                <div class = "col-sm-4">
                    <SingleBlock 
                        block={block1}
                        setBlock={setBlock1}
                    />
                </div>
                <div class = "col-sm-4">
                     <SingleBlock 
                        block={block2}
                        setBlock={setBlock2}
                    />
                </div>
                <div class = "col-sm-4">
                     <SingleBlock 
                        block={block3}
                        setBlock={setBlock3}
                    />
                </div>
            </div>
        </Container>
    );
}

export default Blockchain;
