import React, { Component } from 'react';
import { Container } from 'react-bootstrap';
import SingleBlock from './SingleBlock';
import { mine } from '../MineButton';
import { sha256 } from 'js-sha256';

const defaultNumBlocks = 3;

class Blockchain extends Component {
    constructor(props) {
        super(props);
        this.state = {
            blocks: this.initBlocks(defaultNumBlocks)
        }
    }

    // Update a block in the list.
    setBlock(response) {
        let index = response.index;
        let block = response.block;
        let blocks = [...this.state.blocks];
        blocks[index] = block;
        this.setState({blocks});
    }

    componentDidUpdate(prevProps, prevState) {
        let blocks = [...this.state.blocks];
        //console.log("BLOCKS: ", blocks);
        if (blocks !== prevState.blocks) {
            // If a block is changed update the next block's prevHash.
            for (let i = 1; i < blocks.length; i++) {
                if (blocks[i].prevHash != blocks[i-1].hash) {
                    blocks[i].prevHash = blocks[i-1].hash;
                    this.setBlock({index: i, block: blocks[i]});
                }
            }
        }
    }

    /* Return numBlocks many blocks with pre-filled data */
    initBlocks(numBlocks) {
        // Return either the hashLength many zeros or return the previous 
        // block's hash.
        let hashLength = 64;
        const getPrevHash = (i) => i === 0 ? '0'.repeat(hashLength) : blocks[i - 1].hash;

        let blocks = [];
        for (let i = 0; i < numBlocks; i++) {
            let blockNumber = (i + 1).toString();
            let data = '';
            let prevHash = getPrevHash(i).toString();
            let nonce = mine(blockNumber, data, prevHash).toString();
            let hash = sha256(blockNumber.toString() + nonce.toString() + data.toString() + prevHash.toString());
            let block = {blockNumber, nonce, data, prevHash, hash};
            blocks.push(block);
        }
        //console.log("INIT BLOCKS: ", blocks);
        return blocks;
    }

    render() {
        return (
            <Container fluid>

                <div class = "row justify-content-around" >
                    <div class = "col-sm-4">
                        <SingleBlock index={0} block={this.state.blocks[0]} callback={(response) => this.setBlock(response)} />
                    </div>
                    <div class = "col-sm-4">
                        <SingleBlock index={1} block={this.state.blocks[1]} callback={(response) => this.setBlock(response)} />
                    </div>
                    <div class = "col-sm-4">
                        <SingleBlock index={2} block={this.state.blocks[2]} callback={(response) => this.setBlock(response)} />
                    </div>
                </div>
            </Container>
        );
    }
}

export default Blockchain;
