import { useState, useEffect } from 'react';
import { Button } from 'react-bootstrap';
import { sha256 } from 'js-sha256';
import MineSettings from './MineSettings';

// Parse the mining settings.
const difficulty = MineSettings.difficulty;
const maximumNonce = MineSettings.maximumNonce;
const pattern = MineSettings.pattern;

function mine(blockNumber, data, prevHash="", block, setBlock, setLocalNonce) {
    let sequence = null;
    let hash = "";
    for (let nonce = 0; nonce < maximumNonce; nonce++) {
        hash = sha256(String(blockNumber + nonce + data + prevHash));
        setLocalNonce(nonce);
        if (hash.substr(0, difficulty) === pattern) {
            return nonce;
        }
    }
}

function MineButton({blockNumber, data, prevHash="", block, setBlock}) {
    const [localNonce, setLocalNonce] = useState(0);


    const handleClick = () => {
        let nonce = mine(blockNumber, data, prevHash, block, setBlock);
        setBlock({...block, nonce});
    }

    useEffect(() => {
        if (localNonce % 10 === 0) {
            const timeout = setTimeout(() => {
                setBlock({...block, localNonce});
            }, 1000);

            return () => clearTimeout(timeout);
        }
    }, [localNonce]);

    return (
        <Button 
            onClick={() => handleClick()}
        >
            {'Mine!'}
        </Button>
    );
};

export { MineButton, mine }
