import React, { useState, useEffect } from 'react';
import { Container } from 'react-bootstrap';
import SingleBlock from './SingleBlock';
import DefaultBlocks from './DefaultBlocks';
import { sha256 } from 'js-sha256';

function Blockchain(props) {
    const [block1, setBlock1] = useState(DefaultBlocks.block1);
    const [block2, setBlock2] = useState(DefaultBlocks.block2);
    const [block3, setBlock3] = useState(DefaultBlocks.block3);

    /*
    // Update block1 hash.
    useEffect(() => {
        let newBlock1 = {...block1};
        newBlock1.hash = String(sha256(block1.blockNumber + block1.nonce + block1.data + block1.prevHash));
        setBlock1(newBlock1);
    }, [block1.blockNumber, block1.nonce, block1.data, block1.prevHash]);

    // Update block2 prevHash and hash.
    useEffect(() => {
        let newBlock2 = {...block2};
        newBlock2.prevHash = block1.hash;
        newBlock2.hash = String(sha256(block2.blockNumber + block2.nonce + block2.data + block1.hash));
        setBlock2(newBlock2);
    }, [block2.blockNumber, block2.nonce, block2.data, block1.hash]);

    // Update block3 prevHash and hash.
    useEffect(() => {
        let newBlock3 = {...block3};
        newBlock3.prevHash = block2.hash;
        newBlock3.hash = String(sha256(block3.blockNumber + block3.nonce + block3.data + block2.hash));
        setBlock3(newBlock3);
    }, [block3.blockNumber, block3.nonce, block3.data, block2.hash]);
    */

    // Update block2 prevHash to match block1 hash.
    useEffect(() => {
        setBlock2({...block2, prevHash: block1.hash});
    }, [block1]);

    // Update block3 prevHash to match block2 hash.
    useEffect(() => {
        setBlock3({...block3, prevHash: block2.hash});
    }, [block1]);


    return (
        <Container fluid>
            <div class = "row justify-content-around" >
                <div class = "col-sm-4">
                    <SingleBlock 
                        block={block1}
                        //callback={(block) => setBlock1(block)} 
                        setBlock={setBlock1}
                    />
                </div>
            </div>
        </Container>
    );
}

export default Blockchain;

