import React, { useState, useEffect } from 'react';
import { Container } from 'react-bootstrap';
import SingleBlock from './SingleBlock';
import DefaultBlocks from './DefaultBlocks';

function Blockchain(props) {
    const [block1, setBlock1] = useState(DefaultBlocks.block1);
    const [block2, setBlock2] = useState(DefaultBlocks.block2);
    const [block3, setBlock3] = useState(DefaultBlocks.block3);

    useEffect(() => {
        let newBlock2 = {...block2};
        newBlock2.prevHash = block1.hash;
        setBlock2(newBlock2);
    });

    useEffect(() => {
        let newBlock3 = {...block3};
        newBlock3.prevHash = block2.hash;
        setBlock3(newBlock3);
    });

    return (
        <Container fluid>
            <div class = "row justify-content-around" >
                <div class = "col-sm-4">
                    <SingleBlock 
                        blockIndex={block1.blockIndex} 
                        blockNumber={block1.blockNumber}
                        nonce={block1.nonce} 
                        data={block1.data}
                        prevHash={block1.prevHash}
                        hash={block1.hash}
                        callback={(block) => setBlock1(block)} 
                    />
                </div>
                <div class = "col-sm-4">
                     <SingleBlock 
                        blockIndex={block2.blockIndex} 
                        blockNumber={block2.blockNumber}
                        nonce={block2.nonce} 
                        data={block1.data}
                        prevHash={block2.prevHash}
                        hash={block2.hash}
                        callback={(block) => setBlock2(block)}
                    />
                </div>
                <div class = "col-sm-4">
                     <SingleBlock 
                        blockIndex={block3.blockIndex} 
                        blockNumber={block3.blockNumber}
                        nonce={block3.nonce} 
                        data={block3.data}
                        prevHash={block3.prevHash}
                        hash={block3.hash}
                        callback={(block) => setBlock3(block)}
                    />
                </div>
            </div>
        </Container>
    );
}

export default Blockchain;
