import React, { Component } from 'react';
import { Container, Form } from 'react-bootstrap';
import { sha256 } from 'js-sha256';
import { NumberField, ReadOnlyTextField, TextAreaField } from '../Fields';
import { MineButton, mine } from '../MineButton';
import MineSettings from '../MineSettings';

// Parse the mining settings.
const difficulty = MineSettings.difficulty;
const pattern = MineSettings.pattern;

class SingleBlock extends Component {
    constructor(props) {
        super(props);
        this.state = {
            index: props.index,
            block: props.block,
            color: "#50c878",
            signed: true
        };
        this.setBlockNumber = this.setBlockNumber.bind(this);
        this.setNonce = this.setNonce.bind(this);
        this.setData = this.setData.bind(this);
        this.setPrevHash = this.setPrevHash.bind(this);
        this.setHash = this.setHash.bind(this);
    }

    // Return a hashable block with fields that shouldn't be hashed removed.
    getHashableBlock = (block) => Object.fromEntries(Object.entries(block).filter(([key]) => key !== 'hash'));

    // Return a string by concatenating an objects values as strings.
    getStringValues = (obj) => Object.values(obj).map(value => value.toString()).join('');

    // Return true if the hash is signed and false otherwise.
    isSigned = (hash) => (hash.substr(0, difficulty) === pattern) ? true : false;

    // Return the hash of the given block.
    getHash = (block) => sha256(this.getStringValues(this.getHashableBlock(block)));

    // Define functions for setting the block's fields.
    setBlockNumber = (blockNumber) => this.setState({ block: { ...this.state.block, blockNumber } });
    setNonce = (nonce) => this.setState({ block: { ...this.state.block, nonce } });
    setData = (data) => this.setState({ block: { ...this.state.block, data } });
    setPrevHash = (prevHash) => this.setState({ block: { ...this.state.block, prevHash } });
    setHash = (hash) => this.setState({ block: { ...this.state.block, hash } });

    componentDidUpdate(prevProps, prevState) {
        // If this hash has changed, update the hash and notify the caller.
        const hash = this.getHash(this.state.block);
        if (prevState.block.hash !== hash) {
            this.setHash(hash);

            // Notify the parent of the update.
            this.props.callback({index: this.state.index, block: this.state.block});
        }

        /*
        const prevHash = this.props.prevHash;
        if (prevHash !== prevState.block.prevHash) {
           this.setPrevHash(prevHash); 
        }
        */

        /*
        const signed = this.isSigned(this.state.block.hash);
        if (signed !== prevState.signed) {
            this.setState({signed});
            this.setState({color: signed ? '#50c878' : '#ff0000'});
        }
        */
    }

    render() {
        const { blockNumber, nonce, hash } = this.state.block;
        const prevHash = this.props.block.prevHash;

        return (
            <Container>
                <Container
                    style={{
                        'width':350,
                        'height':500,
                        'background-color':this.state.color,
                    }}
                >
                    <Form>
                        <br/>
                        <NumberField
                            label={'Block Number:'}
                            value={blockNumber}
                            callback={(blockNumber) => this.setBlockNumber(blockNumber)}
                        />
                        <NumberField
                            label={'Nonce:'}
                            value={nonce}
                            callback={(nonce) => this.setNonce(nonce)}
                        />
                        <TextAreaField
                            label={'Data:'}
                            callback={(data) => this.setData(data)}
                        />
                        <ReadOnlyTextField
                            label={'Previous Hash:'}
                            value={prevHash}
                            callback={(prevHash) => this.setPrevHash(prevHash)}
                        />
                        <ReadOnlyTextField
                            label={'Hash:'}
                            value={hash}
                            callback={(hash) => this.setHash(hash)}
                        />
                        <br/>
                        <MineButton
                            blockNumber={this.props.block.blockNumber}
                            data={this.props.block.data}
                            prevHash={this.props.block.prevHash}
                            callback={(nonce) => this.setNonce({nonce})}
                        />
                    </Form>
                </Container>
            </Container>
        );
    }
}

export default SingleBlock;
