import { Button } from 'react-bootstrap';
import { sha256 } from 'js-sha256';
import MineSettings from './MineSettings';

const difficulty = 4;        // number of zeros required at front of hash
const maximumNonce = 500000; // limit the nonce to this so we don't mine too long

function MineButton({blockNumber, data, callback}) {
    /* Find a nonce s.t. the hash of the string concatenation of
     * blockNumber, nonce, and data fields (IN THAT ORDER!) results in a hash
     * that starts with n=difficulty many zeros.
     */
    function mine(blockNumber, data) {
        let pattern = '0'.repeat(difficulty);
        let sequence = null;
        let hash = null;
        for (let nonce = 0; nonce <= maximumNonce; nonce++) {
            sequence = blockNumber.toString() + nonce.toString() + data.toString();
            hash = sha256(sequence);
            if (hash.substr(0, difficulty) === pattern) {
                return nonce;
            }
        }
    }

    return (
        <Button onClick={(event) => {
            callback(mine(blockNumber, data));
            event.preventDefault();
        }}>
            Mine!
        </Button>
    );
};

export default MineButton;
