import React, { useState, useEffect } from 'react';
import { Container } from 'react-bootstrap';
import SingleBlock from './SingleBlock';
import DefaultBlocks from './DefaultBlocks';

function Blockchain(props) {
    /*
    const [block1, setBlock1] = useState(DefaultBlocks.block1);
    const [block2, setBlock2] = useState(DefaultBlocks.block2);
    const [block3, setBlock3] = useState(DefaultBlocks.block3);
    */
    const [blocks, setBlocks] = useState(DefaultBlocks);

    // Update block2 prevHash to match block1 hash.
    /*
    useEffect(() => {
        setBlock2({...block2, prevHash: block1.hash});
    }, [block1]);

    // Update block3 prevHash to match block2 hash.
    useEffect(() => {
        setBlock3({...block3, prevHash: block2.hash});
    }, [block2]);
    */

    function updateBlock(index, block) {
        const newBlocks = [...blocks];
        newBlocks[index] = block;
        setBlocks(newBlocks);
    }

    return (
        <Container fluid>
            <div class = "row justify-content-around" >
                <div class = "col-sm-4">
                    <SingleBlock 
                        index={0}
                        block={blocks[0]}
                        updateBlock={(block) => updateBlock(0, block)}
                    />
                </div>
                <div class = "col-sm-4">
                     <SingleBlock 
                        index={1}
                        block={blocks[1]}
                        updateBlock={(block) => updateBlock(1, block)}
                    />
                </div>
                <div class = "col-sm-4">
                     <SingleBlock 
                        index={2}
                        block={blocks[2]}
                        updateBlock={(block) => updateBlock(2, block)}
                    />
                </div>
            </div>
        </Container>
    );
}

export default Blockchain;
