                        <SingleBlock blockNumber={2} callback={(hash) => this.handleUpdate(hash)} />
                    </div>
                    <div class = "col-sm-4">
                        <SingleBlock blockNumber={3} callback={(hash) => this.handleUpdate(hash)} />
                    </div>
                </div>
            </Container>
        );
    }
}

export default Blockchain;
