import React, { Component } from 'react';
import { Container, Form } from 'react-bootstrap';
import { sha256 } from 'js-sha256';
import get from '../../descriptionLoader';

class Hash extends Component {
    constructor(props) {
        super(props);
        const text = get("https://raw.githubusercontent.com/cse442-21f/FP-Blockchain/main/src/descriptions/Hash.txt?token=ADHANXSOFBVGRZ3YY4U3YXTBUJ7KA");
        console.log(text.indexOf("\n\n"));
        /*
        const text = `Lorem ipsum dolor sit amet, consectetur adipiscing elit, 
        sed do eiusmod tempor incididunt ut labore et dolore magna 
        aliqua.

        Ut enim ad minim veniam, quis nostrud exercitation 
        ullamco laboris nisi ut aliquip ex ea commodo consequat.

        Duis aute irure dolor in reprehenderit in voluptate velit esse cillum 
        dolore eu fugiat nulla pariatur.Excepteur sint occaecat cupidatat non proident, 
        sunt in culpa qui officia deserunt mollit anim id est laborum`
        */
this.state = {
            data: "",
            title: "TEMP TITLE",
            body: text,
        };
                }

        render() {
                return (
            <Container>
             <div>
                {this.state.body.split('\n\n').map(paragraph =>
                    <p>
                        {paragraph.split('\n').reduce((total, line) => [total, <br />, line])}
                    </p>
                )}
            </div>
               <Container>
                    <Form onChange={(event) => {
                        event.preventDefault();
                        this.setState({ data: this.data.value });
                    }}>
                        <Form.Group>
                            <Form.Label>
                                Data: 
                            </Form.Label>
                            <Form.Control 
                                as="textarea"
                                type="text"
                                placeholder="" 
                                ref={(input) => 
                                    this.data = input
                                }
                            />
                        </Form.Group>

                        <Form.Group>
                            <Form.Label>
                                Hash:  
                            </Form.Label>
                                <Form.Control 
                                    type="text" 
                                    value={sha256(this.state.data)} 
                                    plaintext readOnly
                                />
                        </Form.Group>
                    </Form>
                </Container>
            </Container>
        );
    }
}

export default Hash;
