import React, { Component, useState } from 'react';
import { Container, Form } from 'react-bootstrap';
import { sha256 } from 'js-sha256';
import get from '../../descriptionLoader';
import { TitleField, ReadOnlyTextField, BodyField, TextAreaField } from '../Fields';

class Hash extends Component {
    constructor(props) {
        super(props);
        const [title, body] = get("https://cse442-21f.github.io/FP-Blockchain/descriptions/Hash.txt");
        //const [title, body] = ['title', 'body'];
        this.state = {
            data: "",
            hash: sha256(""),
            title,
            body,
        };
    };

    componentDidUpdate(prevProps, prevState) {
        let hash = sha256(this.state.data);
        if (prevState.hash !== hash) {
            this.setState({hash});
        }
    };

    render() {
        return (
            <Container fluid>
                <TitleField titleText={this.state.title} />
                <BodyField bodyText={this.state.body} />
                <Container fluid>
                    <Form>
                        <TextAreaField 
                            label={'Data:'}
                            callback={(data) => {
                                this.setState({data});
                            }}
                        />
                        <ReadOnlyTextField 
                            label={'Hash:'} 
                            value={this.state.hash} 
                        />    
                    </Form>
                </Container>
            </Container>
        );
    };
}

export default Hash;
