import React, { Component } from 'react';
import { Container, Form } from 'react-bootstrap';
import { sha256 } from 'js-sha256';
import { NumberField, ReadOnlyTextField, TextAreaField } from '../Fields';
import { MineButton, mine } from '../MineButton';
import MineSettings from '../MineSettings';

// Parse the mining settings.
const difficulty = MineSettings.difficulty;
const pattern = MineSettings.pattern;

class SingleBlock extends Component {
    constructor(props) {
        super(props);
        this.state = {
            index: props.index,
            block: props.block,
            color: "#50c878",
            signed: true
        };
    }

    // Return a hashable block, i.e. a block with fields that shouldn't be 
    // hashed removed.
    getHashableBlock = (block) => Object.fromEntries(Object.entries(block).filter(([key]) => key !== 'hash'));

    /* Given a block return its hash.
     * Assumes the block only contains fields that should be hashed plus 
     * a hash field, which is excluded.
     */
    getHash(block) {
        // If one exists, remove a 'hash' field from the block.
        let hashableBlock = this.getHashableBlock(block);
        console.log("F BLOCK: ", hashableBlock);
        // And concatenate the remaining field values into a string.
        let str = Object.values(hashableBlock).map(field => field.toString()).join('');
        console.log("STR: ", str);
        let result = sha256(str);
        console.log("RESULT: ", result);
        return sha256(str);
    }

    isSigned(hash) {
        return (hash.substr(0, difficulty) === pattern) ? true : false;
    }

    componentDidUpdate(prevProps, prevState) {
        const prevHash = this.props.prevHash;
        if (prevHash !== prevState.block.preHash) {
           this.setPrevHash(prevHash); 
        }

        // If this hash has changed, update the hash and notify the caller.
        const hash = this.getHash(this.state.block);
        if (prevState.block.hash !== hash) {
            this.setHash(hash);
                }

        if (prevState.block != this.state.block) {
            this.props.callback({index: this.state.index, block: this.state.block});
        }

        let signed = this.isSigned(hash);
        if (prevState.signed !== signed) {
            this.setState({signed});
            this.setState({color: signed ? '#50c878' : '#ff0000'});
        }
    }

    // Define a collection of functions for updating this block's fields.
    setBlockNumber = (blockNumber) => this.setState({ block: { ...this.state.block, blockNumber } });
    setNonce = (nonce) => this.setState({ block: { ...this.state.block, nonce } });
    setData = (data) => this.setState({ block: { ...this.state.block, data } });
    setPrevHash = (prevHash) => this.setState({ block: { ...this.state.block, prevHash } });
    setHash = (hash) => this.setState({ block: { ...this.state.block, hash } });

    render() {
        return (
            <Container>
                <Container
                    style={{
                        'width':350,
                        'height':500,
                        'background-color':this.state.color,
                    }}
                >
                    <Form>
                        <br/>
                        <NumberField
                            label={'Block Number:'}
                            value={this.state.block.blockNumber}
                            callback={(blockNumber) => this.setBlockNumber(blockNumber)}
                        />
                        <NumberField
                            label={'Nonce:'}
                            value={this.state.block.nonce}
                            callback={(nonce) => this.setNonce(nonce)}
                        />
                        <TextAreaField
                            label={'Data:'}
                            callback={(data) => this.setData(data)}
                        />
                        <ReadOnlyTextField
                            label={'Previous Hash:'}
                            value={this.props.block.prevHash}
                            callback={(prevHash) => this.setPrevHash(prevHash)}
                        />
                        <ReadOnlyTextField
                            label={'Hash:'}
                            value={this.state.block.hash}
                            callback={(hash) => this.setHash(hash)}
                        />
                        <br/>
                        <MineButton
                            blockNumber={this.props.block.blockNumber}
                            data={this.props.block.data}
                            prevHash={this.props.block.prevHash}
                            callback={(nonce) => this.setNonce({nonce})}
                        />
                    </Form>
                </Container>
            </Container>
        );
    }
}

export default SingleBlock;
