import React, { useState, useEffect } from 'react';
import { Container, Form } from 'react-bootstrap';
import { sha256 } from 'js-sha256';
import { NumberField, ReadOnlyTextField, TextAreaField } from '../Fields';
import { MineButton, mine } from '../MineButton';
import MineSettings from '../MineSettings';

// Parse the mining settings.
const difficulty = MineSettings.difficulty;
const pattern = MineSettings.pattern;

// Define component globals.                                                      
const GREEN = '#50C878';                                                          
const RED = '#FF0000';

function SingleBlock(props) {
    const [blockIndex, setBlockIndex] = useState(props.blockIndex);
    const [blockNumber, setBlockNumber] = useState(String(props.blockNumber));
    const [nonce, setNonce] = useState(String(props.nonce));
    const [data, setData] = useState(String(props.data));
    const [prevHash, setPrevHash] = useState(String(props.prevHash));
    const [hash, setHash] = useState(String(props.hash));
    const [color, setColor] = useState(GREEN);
    const [isSigned, setIsSigned] = useState(true);

    useEffect(() => {
        setHash(String(sha256(blockNumber + nonce + data + prevHash)));
    }, [blockNumber, nonce, data, prevHash]);

    useEffect(() => {
        setIsSigned(hash.substr(0, difficulty) === pattern ? true : false);
    }, [hash]);

    useEffect(() => {
        setColor(isSigned ? GREEN : RED);
    }, [isSigned]);

    useEffect(() => {
        /*
        const response = {
            blockIndex, 
            blockNumber, 
            nonce,
            data,
            prevHash, 
            hash
        };
        */
        const response = {blockIndex, hash};
        props.callback(response);
    }, [blockIndex, blockNumber, nonce, data, prevHash, hash]);

    return (
        <Container>
            <Container
                style={{
                    'width':350,
                    'height':500,
                    'background-color':color
                }}
            >
                <Form>
                    <br/>
                    <NumberField
                        label={'Block Number:'}
                        value={blockNumber}
                        callback={(blockNumber) => setBlockNumber(String(blockNumber))}
                    />
                    <NumberField
                        label={'Nonce:'}
                        value={nonce}
                        callback={(nonce) => setNonce(String(nonce))}
                    />
                    <TextAreaField
                        label={'Data:'}
                        callback={(data) => setData(String(data))}
                    />
                    <ReadOnlyTextField
                        label={'Prev:'}
                        value={props.prevHash}
                        callback={(prevHash) => setPrevHash(String(prevHash))}
                    />
                    <ReadOnlyTextField
                        label={'Hash:'}
                        value={hash}
                        callback={(hash) => setHash(String(hash))}
                    />
                    <br/>
                    <MineButton
                        blockNumber={blockNumber}
                        data={data}
                        prevHash={prevHash}
                        callback={(nonce) => setNonce(String(nonce))}
                    />
                </Form>
            </Container>
        </Container>
    );
}

export default SingleBlock;
