import React, { Component, useState } from 'react';
import { Container, Form } from 'react-bootstrap';
import { sha256 } from 'js-sha256';
import get from '../../descriptionLoader';
import DataField from '../DataField.js';

class Hash extends Component {
    constructor(props) {
        super(props);
        const [title, body] = get("https://cse442-21f.github.io/FP-Blockchain/descriptions/Hash.txt");
        //const [title, body] = ['title', 'body'];
        this.state = {
            data: "",
            title,
            body,
        };
    }

    dataFieldCallback = (dataFieldContents) => {
        this.setState({ data: dataFieldContents });
    };

    render() {
        return (
            <Container fluid>
                <h2>
                    {this.state.title}
                </h2>
                <div>
                    {this.state.body.split('\n\n').map(paragraph =>
                        <p>
                            {paragraph.split('\n').reduce((total, line) => [total, <br />, line])}
                        </p>
                    )}
                </div>
                <Container fluid>
                    <Form onChange={(event) => {
                        event.preventDefault();
                        this.setState({ data: this.data.value });
                    }}>
                        <DataField callback={this.dataFieldCallback} />
                        <Form.Group>
                            <Form.Label>
                                Hash:  
                            </Form.Label>
                                <Form.Control 
                                    type="text" 
                                    value={sha256(this.state.data)} 
                                    readOnly
                                />
                        </Form.Group>
                    </Form>
                </Container>
            </Container>
        );
    }
}

export default Hash;
