import React, { Component } from 'react';
import { Container } from 'react-bootstrap';
import SingleBlock from './SingleBlock';
import { TitleField, BodyField } from '../Fields';

const [title, body] = ["Blockchain [WORK IN PROGRESS] ", "Blockchain's Description"];

class Blockchain extends Component {
    constructor(props) {
        super(props);
        this.state = {
            hashes: ["", "", "", ""],
        }
    }

    handleUpdate(response) {
        console.log(response.blockNumber);
        let hashes = this.state.hashes;
        let index = response.blockNumber;
        let hash = response.hash;
        hashes[index] = hash;
        this.setState({hashes}); 
    }

    componentDidUpdate() {

    }

    render() {
        return (
            <Container fluid>
                <TitleField titleText={title} />
                <BodyField bodyText={body} />
                <div class = "row justify-content-around" >
                    <div class = "col-sm-4">
                        <SingleBlock blockNumber={1} hash={this.state.hashes[1]} callback={(response) => this.handleUpdate(response)} />
                    </div>
                    <div class = "col-sm-4">
                        <SingleBlock blockNumber={2} hash={this.state.hashes[2]} callback={(hash) => this.handleUpdate(hash)} />
                    </div>
                    <div class = "col-sm-4">
                        <SingleBlock blockNumber={3} hash={this.state.hashes[3]} callback={(hash) => this.handleUpdate(hash)} />
                    </div>
                </div>
            </Container>
        );
    }
}

export default Blockchain;
