import React, { Component } from 'react';
import { Container, Form } from 'react-bootstrap';
import { sha256 } from 'js-sha256';
import * as $ from 'jquery';

class Hash extends Component {
    constructor(props) {
        super(props);
        this.state = {
            data: "",
        };
    }

   getExplanation(url) {
        const content = (() => {
            var content = null;
            $.ajax({
                'async': false,
                'url': url,
                'dataType': "text",
                'success': (data) => {
                    content = data;
                }
            });
            return content;
        })();
        return content;
    }

    render() {
        const url = "https://raw.githubusercontent.com/cse442-21f/FP-Blockchain/main/src/descriptions/Hash.txt?token=ADHANXRVBEMRDJZ3HKHVPF3BUFVKY"; 
        const description = this.getExplanation(url);
        console.log(description);
        return (
            <Container>
                {description}

                <Container>
                    <Form onChange={(event) => {
                        event.preventDefault();
                        this.setState({ data: this.data.value });
                    }}>
                        <Form.Group>
                            <Form.Label>
                                Data: 
                            </Form.Label>
                            <Form.Control 
                                as="textarea"
                                type="text"
                                placeholder="" 
                                ref={(input) => 
                                    this.data = input
                                }
                            />
                        </Form.Group>

                        <Form.Group>
                            <Form.Label>
                                Hash:  
                            </Form.Label>
                                <Form.Control 
                                    type="text" 
                                    value={sha256(this.state.data)} 
                                    plaintext readOnly
                                />
                        </Form.Group>
                    </Form>
                </Container>
            </Container>
        );
    }
}

export default Hash;
