import React, { Component } from 'react';
import { Container, Form, Button } from 'react-bootstrap';
import { sha256 } from 'js-sha256';
import { NumberField, HashField, TextAreaField } from './Fields';
import MineButton from './MineButton';
import get from '../descriptionLoader';

class SingleBlock extends Component {
    constructor(props) {
        super(props);
        this.state = {
            pattern: '0000',
            blockNumber: 1,
            nonce: 72608,
            data: "",
            hash: sha256("1" + "72608" + ""),
            color: "#50C878" // Green
        };
        this.handleColorChange = this.handleColorChange.bind(this);
    }

    handleColorChange() {
        var col = this.state.color
        this.props.getColor(col)        
    }

    getHashData() {
        return this.state.blockNumber.toString() + this.state.nonce.toString() + this.state.data.toString();
    }

    // TODO Abstract and generalize
    isSigned() {
        return this.state.hash.substr(0, 4) === '0000';
    }

    getHashPattern() {
        return this.state.hash.substr(0, 4);
    }

    render() {
        return (
            <Container fluid>
                <Form onChange={(event) => {
                    console.log("HASH PATTERN: ", this.getHashPattern());
                    if (this.isSigned()) {
                        console.log("SIGNED");
                    } else {
                        console.log("NOT SIGNED");
                    }
                }}>
                    <br/>
                    <NumberField 
                        name={'Block Number:'} 
                        value={this.state.blockNumber} 
                        callback={(blockNumber) => this.setState({blockNumber})} 
                    />
                    <NumberField 
                        name={'Nonce:'} 
                        value={this.state.nonce} 
                        callback={(nonce) => this.setState({nonce})} 
                    />
                    <TextAreaField 
                        name={'Data:'} 
                        callback={(data) => this.setState({data})} 
                    />
                    <HashField data={this.getHashData()} />
                    <br/>
                    <MineButton 
                        blockNumber={this.state.blockNumber} 
                        data={this.state.data} 
                        callback={(nonce) => this.setState({nonce})}
                    />
                    <p></p>
                </Form>
            </Container>
        );
    }
}

export default SingleBlock;
