import React, { Component } from 'react';
import { Container, Form, Button } from 'react-bootstrap';
import { sha256 } from 'js-sha256';
//import {render} from "react-dom";
//import get from '../../descriptionLoader';

let difficulty = 4;        // number of zeros required at front of hash
let maximumNonce = 500000; // limit the nonce to this so we don't mine too long

class Block extends Component {
    constructor(props) {
        super(props);
        const [title, body] = ["Tokens [WORK IN PROGRESS] ", "Tokens' Description"];
        this.state = {
            pattern: '0'.repeat(difficulty),
            blockNumber: 1,
            nonce: 72608,
            data: "",
            hash: sha256("1" + "72608" + ""),
            title,
            body,
            //green
            color: "#50C878"
        };
        this.getSequence = this.getSequence.bind(this);
        this.mine = this.mine.bind(this);
        this.handleColorChange = this.handleColorChange.bind(this);
    }

    handleColorChange() {
        var col = this.state.color
        this.props.getColor(col)
    }

    getSequence() {
        return (this.blockNumber.value.toString() + this.nonce.value.toString() + this.data.value.toString());
    }

    /* Find a nonce s.t. the hash of the string concatenation of
     * blockNumber, nonce, and data fields (IN THAT ORDER!) results in a hash
     * that starts with n=difficulty many zeros.
     * Updates the state with the found nonce and hash.
     */
    mine(blockNumber, data) {
        let sequence = null;
        let hash = null;
        for (let nonce = 0; nonce <= maximumNonce; nonce++) {
            sequence = blockNumber.toString() + nonce.toString() + data.toString();
            hash = sha256(sequence);
            if (hash.substr(0, difficulty) === this.state.pattern) {
                this.setState({hash, nonce});
                return;
            }
        }
        this.setState({color: "#50C878"});
        this.handleColorChange(this.state.color);

    }

    render() {
        return (
            <Container fluid>
                <h2>
                    {this.state.title}
                </h2>
                <div>
                    {this.state.body.split('\n\n').map(paragraph =>
                        <p>
                            {paragraph.split('\n').reduce((total, line) => [total, <br />, line])}
                        </p>
                    )}
                </div>
                <Container fluid>
                    <Form  onChange={(event) => {
                        event.preventDefault();
                        this.setState({
                            blockNumber: this.blockNumber.value,
                            nonce: this.nonce.value,
                            data: this.data.value,
                            hash : sha256(this.getSequence()),
                            //red
                            color: "#F75D59"
                        });
                    }}>
                        <Form.Group>
                            <Form.Label>
                                Block Number:
                            </Form.Label>
                            <Form.Control
                                type="number"
                                ref={(input) =>
                                    this.blockNumber = input
                                }
                                value = {this.state.blockNumber}
                            />
                        </Form.Group>
                        <Form.Group>
                            <Form.Label>
                                Nonce:
                            </Form.Label>
                            <Form.Control
                                type="number"
                                ref={(input) =>
                                    this.nonce = input
                                }
                                value = {this.state.nonce}
                            />
                        </Form.Group>
                        <Form.Group>
                            <Form.Label>
                                Data:
                            </Form.Label>
                            <Form.Control
                                as="textarea"
                                type="text"
                                placeholder=""
                                ref={(input) =>
                                    this.data = input
                                }
                                value = {this.state.data}
                            />
                        </Form.Group>
                        <Form.Group>
                            <Form.Label>
                                Hash:
                            </Form.Label>
                            <Form.Control
                                type="text"
                                value= {this.state.hash}
                                readOnly
                            />
                        </Form.Group>
                    </Form>
                    <Button onClick={(event) => {
                        event.preventDefault();
                        this.mine(this.state.blockNumber, this.state.data);
                    }}>
                        Press
                    </Button>
                </Container>
            </Container>
        );
    }
}

export default Block;
