import React, { Component, useState } from 'react';
import { Container, Form } from 'react-bootstrap';
import { sha256 } from 'js-sha256';
import get from '../../descriptionLoader';
import { TitleField, BodyField, DataField, HashField } from '../Fields';

class Hash extends Component {
    constructor(props) {
        super(props);
        const [title, body] = get("https://cse442-21f.github.io/FP-Blockchain/descriptions/Hash.txt");
        //const [title, body] = ['title', 'body'];
        this.state = {
            data: "",
            hash: sha256(""),
            title,
            body,
        };
    }

    render() {
        return (
            <Container fluid>
                <TitleField titleText={this.state.title} />
                <BodyField bodyText={this.state.body} />
                <Container fluid>
                    <Form>
                        <DataField 
                            callback={(data) => {
                                this.setState({data});
                                this.setState({hash: sha256(data)});
                                console.log("data: ", this.state.data);
                                console.log("hash: ", this.state.hash);
                            }}
                        />
                        <HashField value={this.state.hash} />    
                    </Form>
                </Container>
            </Container>
        );
    }
}

export default Hash;
