    useEffect(() => {
        const timeout = setTimeout(() => {
        }, 1000);

        return () => clearTimeout(timeout);
    }, [block]);

    return (
        <Button 
            onClick={() => mine(blockNumber, data, prevHash, block, setBlock)}
        >
            {'Mine!'}
        </Button>
    );
};

