function MineButton({blockNumber, data, prevHash="", block, setBlock}) {
    const [localNonce, setLocalNonce] = useState(0);

    useEffect(() => {
        if (localNonce % 10 === 0) {
            const timeout = setTimeout(() => {
                setBlock({...block, localNonce});
            }, 1000);

            return () => clearTimeout(timeout);
        }
    }, [localNonce]);

    return (
        <Button 
            onClick={() => mine(blockNumber, data, prevHash, block, setBlock)}
        >
            {'Mine!'}
        </Button>
    );
};

