import { useState, useEffect } from 'react';
import { Button } from 'react-bootstrap';
import { sha256 } from 'js-sha256';
import MineSettings from './MineSettings';

// Parse the mining settings.
const difficulty = MineSettings.difficulty;
const maximumNonce = MineSettings.maximumNonce;
const pattern = MineSettings.pattern;

/* Find a nonce s.t. the hash of the string concatenation of
 * blockNumber, nonce, and data fields (IN THAT ORDER!) results in a hash
 * that starts with n=difficulty many zeros.
 */
function mine(blockNumber, data, prevHash="") {
    let sequence = null;
    let hash = null;
    for (let nonce = 0; nonce <= maximumNonce; nonce++) {
        sequence = blockNumber.toString() + nonce.toString() + data.toString() + prevHash.toString();
        hash = sha256(sequence);
        /* HERE */
        if (hash.substr(0, difficulty) === pattern) {
            return nonce;
        }
    }
}

function simulateMining() {
    return new Promise((resolve) => setTimeout(resolve, 2000));
}


function MineButton({input, callback}) {
    const [isLoading, setLoading] = useState(false);

    useEffect(() => {
        if (isLoading) {
            simulateMining().then(() => {
                setLoading(false);
            }).then(() => {
                callback(mine(blockNumber, data, prevHash));
            });
        }
    }, [isLoading]);

    const handleClick = () => {
        setLoading(true);
    }

    return (
        <Button 
            disabled={isLoading}
            onClick={!isLoading ? handleClick : null}
        >
            {!isLoading ? 'Mine!' : 'Mining...'}
        </Button>
    );
};

export { MineButton, mine }
