import React, { Component } from 'react';
import { Container, Form } from 'react-bootstrap';
import { sha256 } from 'js-sha256';
// import * as $ from 'jquery';

class Hash extends Component {
    constructor(props) {
        super(props);
        this.state = {
            data: "",
            text: ``,
        };
    }

    render() {
        return (
            <Container>
                <Form onChange={(event) => {
                    event.preventDefault();
                    this.setState({ data: this.data.value });
                }}>
                    <Form.Group>
                        <Form.Label>
                            Data: 
                        </Form.Label>
                        <Form.Control 
                            as="textarea"
                            type="text"
                            placeholder="" 
                            ref={(input) => 
                                this.data = input
                            }
                        />
                    </Form.Group>

                    <Form.Group>
                        <Form.Label>
                            Hash:  
                        </Form.Label>
                            <Form.Control 
                                type="text" 
                                value={sha256(this.state.data)} 
                                plaintext readOnly
                            />
                    </Form.Group>
                </Form>
            </Container>
        );
    }
}

export default Hash;
