import { useState, useEffect } from 'react';
import { Button } from 'react-bootstrap';
import { sha256 } from 'js-sha256';
import MineSettings from './MineSettings';

// Parse the mining settings.
const difficulty = MineSettings.difficulty;
const maximumNonce = MineSettings.maximumNonce;
const pattern = MineSettings.pattern;

function MineButton({isSigned, callback, block, setBlock}) {
    const [nonce, setNonce] = useState(0);
    const [isMining, setIsMining] = useState(false);

    useEffect(() => {
        if (isMining) {
            const interval = setInterval(() => {
                setNonce(nonce + 1);
                console.log("NONCE", nonce);
            }, 1000);

            return () => clearInterval(interval);
        }
    }, [nonce]);

    return (
        <Button 
            onClick={() => setIsMining(true)}
        >
            {'Mine!'}
        </Button>
    );
};

export { MineButton }
