import { useEffect } from 'react';
import { Button } from 'react-bootstrap';
import { sha256 } from 'js-sha256';
import MineSettings from './MineSettings';

// Parse the mining settings.
const difficulty = MineSettings.difficulty;
const maxNonce = MineSettings.maximumNonce;
const pattern = MineSettings.pattern;

function* nonceIter(blockNumber, data, prevHash) {
    const hash = nonce => sha256(blockNumber + nonce + data + prevHash);
    const isSigned = hash => hash.substr(0, difficulty) === pattern;
    let nonce = 0;
    while (!isSigned(hash(nonce))) {
        yield nonce;
        nonce++;
    }
}

function MineButton({blockNumber, data, prevHash, callback, block, setBlock}) {
    /*
    const mine = (nonce = 0) => {
        const hash = nonce => sha256(blockNumber + nonce + data + prevHash);
        const isSigned = hash => hash.substr(0, difficulty) === pattern;
        while (!isSigned(hash(nonce))) {
            nonce++;
        }
        return nonce;
    }   
    */

    const handleClick = () => {
        let nonce = 0;
        const interval = setInterval(() => {
            nonce = nonceIter(blockNumber, data, prevHash).next().value;
            if (nonce % 10 === 0) {
                setBlock({...block, nonce}); 
            }
        }, 1000);
        return () => clearInterval(interval);
    }

    return (
        <Button 
            onClick={() => handleClick()}
        >
            {'Mine!'}
        </Button>
    );
};

export { MineButton }
