import React, { useState, useEffect } from 'react';
import { Container, Form } from 'react-bootstrap';
import { NumberField, ReadOnlyTextField, TextAreaField } from '../Fields';
import { MineButton } from '../MineButton';
import MineSettings from '../MineSettings';

// Parse the mining settings.
const difficulty = MineSettings.difficulty;
const pattern = MineSettings.pattern;

// Define component globals.                                                      
const GREEN = '#50C878';                                                          
const RED = '#FF0000';

function SingleBlock({block, setBlock1, setBlock2}) {
    /*
    const [blockNumber, setBlockNumber] = useState(block.blockNumber);
    const [nonce, setNonce] = useState(block.nonce);
    const [data, setData] = useState(block.data);
    */
    const [color, setColor] = useState(GREEN);
    const [isSigned, setIsSigned] = useState(true);

    // Set isSigned.
    useEffect(() => {
        setIsSigned(block.hash.substr(0, difficulty) === pattern ? true : false);
    }, [block.hash]);

    // Set color.
    useEffect(() => {
        setColor(isSigned ? GREEN : RED);
    }, [isSigned]);

    // Notify the caller of the changes. 
    /*
    useEffect(() => {
        //callback({...block, blockNumber, nonce, data});
        setBlock1({...block, blockNumber, nonce, data});
    }, [blockNumber, nonce, data]);
    */

    return (
        <Container>
            <Container
                style={{
                    'width':350,
                    'height':500,
                    'background-color':color
                }}
            >
                <Form>
                    <br/>
                    <NumberField
                        label={'Block Number:'}
                        value={block.blockNumber}
                        callback={(blockNumber) => setBlock1({...block, blockNumber})}
                    />
                    <NumberField
                        label={'Nonce:'}
                        value={block.nonce}
                        callback={(nonce) => setBlock1({...block, nonce})}
                    />
                    <TextAreaField
                        label={'Data:'}
                        callback={(data) => setBlock1({...block, data})}
                    />
                    <ReadOnlyTextField
                        label={'Previous Hash:'}
                        value={block.prevHash}
                    />
                    <ReadOnlyTextField
                        label={'Hash:'}
                        value={block.hash}
                    />
                    <br/>
                    <MineButton
                        blockNumber={block.blockNumber}
                        data={block.data}
                        prevHash={block.prevHash}
                        callback={(nonce) => setBlock1({...block, nonce})}
                    />
                </Form>
            </Container>
        </Container>
    );
}

export default SingleBlock;
