import React, { useState, useEffect } from 'react';
import { Container } from 'react-bootstrap';
import SingleBlock from './SingleBlock';
import DefaultBlocks from './DefaultBlocks';
import { sha256 } from 'js-sha256';

function Blockchain(props) {
    const [block1, setBlock1] = useState(DefaultBlocks.block1);
    const [block2, setBlock2] = useState(DefaultBlocks.block2);
    const [block3, setBlock3] = useState(DefaultBlocks.block3);

    useEffect(() => {
        let newBlock2 = {...block2};
        newBlock2.prevHash = block1.hash;
        newBlock2.hash = String(sha256(block2.blockNumber + block2.nonce + block2.data + newBlock2.prevHash));
        setBlock2(newBlock2);
    });

    useEffect(() => {
        let newBlock3 = {...block3};
        newBlock3.prevHash = block2.hash;
        newBlock3.hash = String(sha256(block3.blockNumber + block3.nonce + block3.data + newBlock3.prevHash));
        setBlock3(newBlock3);
    });

    return (
        <Container fluid>
            <div class = "row justify-content-around" >
                <div class = "col-sm-4">
                    <SingleBlock 
                        block={block1}
                        setBlockNumber={(blockNumber) => setBlock1({...block1, blockNumber})}
                        setNonce={(nonce) => setBlock1({...block1, nonce})}
                        setData={(data) => setBlock1({...block1, data})}
                        setPrevHash={(prevHash) => setBlock1({...block1, prevHash})}
                        hash={(hash) => setBlock1({...block1, hash})}
                    />
                </div>
                <div class = "col-sm-4">
                     <SingleBlock 
                        block={block2}
                        callback={(block) => setBlock2(block)}
                    />
                </div>
                <div class = "col-sm-4">
                     <SingleBlock 
                        block={block3}
                        callback={(block) => setBlock3(block)}
                    />
                </div>
            </div>
        </Container>
    );
}

export default Blockchain;
