import React, { Component } from 'react';
import { Container, Form } from 'react-bootstrap';
import { sha256 } from 'js-sha256';
import { TitleField, BodyField, NumberField, ReadOnlyTextField, TextAreaField } from '../Fields';
import mineButton from '../MineButton';
import MineSettings from '../MineSettings';
import get from '../../descriptionLoader';

// Parse the mining settings.
const difficulty = MineSettings.difficulty;
const pattern = MineSettings.pattern;

// Load the component's title and body text.
const [title, body] = get("https://cse442-21f.github.io/FP-Blockchain/descriptions/Block.txt");

// Define component globals.
const GREEN = '#50C878';
const RED = 'FF0000';

const BLOCK_WIDTH = 400;
const BLOCK_HEIGHT = 410;

class SingleBlock extends Component {
    constructor(props) {
        super(props);
        this.state = {
            blockNumber: 1,
            nonce: 72608,
            data: "",
            hash: "",
            color: GREEN,
            signed: true
        };
    }

    getHash() {
        return sha256(this.state.blockNumber.toString() + this.state.nonce.toString() + this.state.data.toString());
    }

    getHashPattern() {
        return this.state.hash.substr(0, difficulty);
    }

    isSigned() {
        return (this.getHashPattern() === pattern) ? true : false;
    }

    componentDidMount() {
        let hash = this.getHash();
        this.setState({hash});
    }

    componentDidUpdate(prevProps, prevState) {
        let hash = this.getHash();
        if (prevState.hash !== hash) {
            this.setState({hash});
        }

        let signed = this.isSigned();
        if (prevState.signed !== signed) {
            this.setState({signed});
            this.setState({color: signed ? GREEN : RED});
        }
    }

    render() {
        return (
            <Container>
                <TitleField titleText={title} />
                <BodyField bodyText={body} />
                <Container 
                    style={{
                        'width':BLOCK_WIDTH,
                        'height':BLOCK_HEIGHT,
                        'background-color':this.state.color
                    }}
                >
                    <Form>
                        <br/>
                        <NumberField 
                            label={'Block Number:'} 
                            value={this.state.blockNumber} 
                            callback={(blockNumber) => this.setState({blockNumber})}
                        />
                        <NumberField 
                            label={'Nonce:'} 
                            value={this.state.nonce} 
                            callback={(nonce) => this.setState({nonce})} 
                        />
                        <TextAreaField 
                            label={'Data:'} 
                            callback={(data) => this.setState({data})}
                        />
                        <ReadOnlyTextField 
                            label={'Hash:'}
                            value={this.state.hash}
                        />
                        <br/>
                        <mineButton 
                            blockNumber={this.state.blockNumber} 
                            data={this.state.data} 
                            callback={(nonce) => this.setState({nonce})}
                        />
                    </Form>
                </Container>
            </Container>
        );
    }
}

export default SingleBlock;
