import React, { Component } from 'react';
import { Container, Form } from 'react-bootstrap';
import { sha256 } from 'js-sha256';
import get from '../../descriptionLoader';

class Hash extends Component {
    constructor(props) {
        super(props);
        const [title, body] = get("https://raw.githubusercontent.com/cse442-21f/FP-Blockchain/main/src/descriptions/Hash.txt?token=ADHANXSOFBVGRZ3YY4U3YXTBUJ7KA");
        this.state = {
            data: "",
            title,
            body,
        };
    }

    render() {
        return (
            <Container>
                <h2>
                    {this.state.title}
                </h2>
                <div>
                    {this.state.body.split('\n\n').map(paragraph =>
                        <p>
                            {paragraph.split('\n').reduce((total, line) => [total, <br />, line])}
                        </p>
                    )}
                </div>
                <Container>
                    <Form onChange={(event) => {
                        event.preventDefault();
                        this.setState({ data: this.data.value });
                    }}>
                    <Form.Group>
                        <Form.Label>
                            Data: 
                        </Form.Label>
                        <Form.Control 
                            as="textarea"
                            type="text"
                            placeholder="" 
                            ref={(input) => 
                                this.data = input
                            }
                        />
                    </Form.Group>
                    <Form.Group>
                        <Form.Label>
                            Hash:  
                        </Form.Label>
                        <Form.Control 
                            type="text" 
                            value={sha256(this.state.data)} 
                            plaintext readOnly
                        />
                    </Form.Group>
                    </Form>
                </Container>
            </Container>
        );
    }
}

export default Hash;
