import React, { Component } from 'react';                                         
import { Form } from 'react-bootstrap';                                           
import { sha256 } from 'js-sha256';

class DataField extends Component {                                               
    render() {                                                                    
        return (                                                                  
            <Form.Group>                                                          
                <Form.Label>                                                      
                    Data:                                                         
                </Form.Label>                                                     
                <Form.Control                                                     
                    as="textarea"                                                 
                    type="text"                                                   
                    placeholder=""                                                
                    onChange={(event) => {                                        
                        this.props.callback(event.target.value);                  
                        event.preventDefault();                                   
                    }}                                                            
                />                                                                
            </Form.Group>                                                         
        );                                                                        
    };                                                                            
}                                                                                 

function BodyField({bodyText}) {
    return (
        <div>
            {bodyText.split('\n\n').map(paragraph =>
            <p>
                {paragraph.split('\n').reduce((total, line) => [total, <br />, line])}
            </p>
            )}
        </div>
    );
}

function TitleField({titleText}) {
    return (
        <h2>
            {titleText}
        </h2>
    );
}

class HashField extends Component {
    render() {
        return (
            <Form.Group>
                <Form.Label>
                    Data:
                </Form.Label>
                <Form.Control
                    type="text"
                    placeholder=""
                    value={sha256(this.props.data)}
                    readOnly
                />
            </Form.Group>
        );
    };
}

export { DataField, BodyField, TitleField, HashField }
