import React, { useState, useEffect } from 'react';
import { Container, Form } from 'react-bootstrap';
import { sha256 } from 'js-sha256';
import { TitleField, BodyField, NumberField, ReadOnlyTextField, TextAreaField } from '../Fields';
import { MineButton } from '../MineButton';
import MineSettings from '../MineSettings';
import description from './description';
import { SwitchButton } from '../Switch';
import DefaultBlocks from '../Blockchain/DefaultBlocks';

// Parse the mining settings.
const difficulty = MineSettings.difficulty;
const pattern = MineSettings.pattern;

// Define component globals.
const GREEN = '#50C878';
const RED = '#FF0000';

const BLOCK_WIDTH = 350;
const BLOCK_HEIGHT = 450;

const block = DefaultBlocks.block1;

function Block(props) {
    const [blockNumber, setBlockNumber] = useState(block.blockNumber);
    const [nonce, setNonce] = useState(block.nonce);
    const [data, setData] = useState(block.data);
    const [hash, setHash] = useState(block.hash);
    const [color, setColor] = useState(GREEN);
    const [isSigned, setIsSigned] = useState(true);

    useEffect(() => {
        setHash(String(sha256(blockNumber + nonce + data)));
    }, [blockNumber, nonce, data]);

    useEffect(() => {
        setIsSigned(hash.substr(0, difficulty) === pattern ? true : false);
    }, [hash]);

    useEffect(() => {
        setColor(isSigned ? GREEN : RED);
    }, [isSigned]);

    return (
        <Container>
            <TitleField titleText={description.title} />
            <BodyField bodyText={description.body} />
            <Container 
                style={{
                    'width':BLOCK_WIDTH,
                    'height':BLOCK_HEIGHT,
                    'background-color':color
                }}
            >
                <Form>
                    <br/>
                    <NumberField 
                        label={'Block Number:'} 
                        value={blockNumber} 
                        callback={(blockNumber) => setBlockNumber(String(blockNumber))}
                    />
                    <NumberField 
                        label={'Nonce:'} 
                        value={nonce} 
                        callback={(nonce) => setNonce(String(nonce))} 
                    />
                    <TextAreaField 
                        label={'Data:'} 
                        callback={(data) => setData(String(data))}
                    />
                    <ReadOnlyTextField 
                        label={'Hash:'}
                        value={hash}
                    />
                    <br/>
                    <MineButton 
                        blockNumber={blockNumber} 
                        data={data} 
                        callback={(nonce) => setNonce(String(nonce))}
                    />
                    <SwitchButton
                    />
                </Form>
            </Container>
        </Container>
    );
}

export default Block;
