import React, { Component } from 'react';
import { Container, Form, Button } from 'react-bootstrap';
import { sha256 } from 'js-sha256';
import { NumberField, ReadOnlyTextField, TextAreaField } from './Fields';
import MineButton from './MineButton';
import get from '../descriptionLoader';

class SingleBlock extends Component {
    constructor(props) {
        super(props);
        this.state = {
            blockNumber: 1,
            nonce: 72608,
            data: "",
            hash: sha256("1" + "72608" + ""),
            color: "#50C878" // Green
        };
        this.handleColorChange = this.handleColorChange.bind(this);
    }

    handleColorChange() {
        var col = this.state.color
        this.props.getColor(col)        
    }

    updateBlockNumber(blockNumber) {
        this.setState({blockNumber});
        let hash = sha256(blockNumber.toString() + this.state.nonce.toString() + this.state.data.toString());
        this.setState({hash});
    }

    updateNonce(nonce) {
        this.setState({nonce});
        let hash = sha256(this.state.blockNumber.toString() + nonce.toString() + this.state.data.toString());
        this.setState({hash});
    }

    updateData(data) {
        this.setState({data});
        let hash = sha256(this.state.blockNumber.toString() + this.state.nonce.toString() + data.toString());
        this.setState({hash});
    };

    render() {
        return (
            <Container fluid>
                <Form 
                    onChange={(event) => {
                        if (this.state.hash.substr(0, 4) === "0000") {
                            console.log("SIGNED");
                        } else {
                            console.log("UNSIGNED");
                        }
                    }}
                >
                    <br/>
                    <NumberField 
                        label={'Block Number:'} 
                        value={this.state.blockNumber} 
                        callback={(blockNumber) => this.updateBlockNumber(blockNumber)} 
                    />
                    <NumberField 
                        label={'Nonce:'} 
                        value={this.state.nonce} 
                        callback={(nonce) => this.updateNonce(nonce)} 
                    />
                    <TextAreaField 
                        label={'Data:'} 
                        callback={(data) => this.updateData(data)}
                    />
                    <ReadOnlyTextField 
                        label={'Hash:'}
                        value={this.state.hash}
                    />
                    <br/>
                    <MineButton 
                        blockNumber={this.state.blockNumber} 
                        data={this.state.data} 
                        callback={(nonce) => this.updateNonce(nonce)}
                    />
                    <p></p>
                </Form>
            </Container>
        );
    }
}

export default SingleBlock;
