function simulateMine(i) {
    setTimeout(() => {
        console.log("I=", i);
    }, 2000);
}

function handleOnClick(nonces, block, setBlock) {
    for (let i = 0; i < nonces.length; i++) {
        simulateMine(i);
    }  
};
*/

function MineButton({blockNumber, data, prevHash="", block, setBlock}) {
    return (
        <Button 
            onClick={() => setBlock({...block, nonce: mineIter.next().value})}
        >
            {'Mine!'}
        </Button>
    );
};

export { MineButton, mine }
