import { useState, useEffect } from 'react';
import { Button } from 'react-bootstrap';
import { sha256 } from 'js-sha256';
import MineSettings from './MineSettings';

// Parse the mining settings.
const difficulty = MineSettings.difficulty;
const maximumNonce = MineSettings.maximumNonce;
const pattern = MineSettings.pattern;

function mine(blockNumber, data, prevHash="", block, setBlock) {
    let sequence = null;
    for (let nonce = 0; nonce < maximumNonce; nonce++) {
        hash = sha256(String(blockNumber + (++nonce) + data + prevHash));
        //setTimeout(() => {setBlock({...block, nonce})}, 1000);
        console.log("NONCE OUT", nonce);
        setTimeout(() => {
            //setBlock({...block, nonce});
            console.log("NONCE IN", nonce);
        }, 1000 * nonce);
    }
}
