import React, { Component } from 'react';
import { Form } from 'react-bootstrap';
import { sha256 } from 'js-sha256';

// Return the component title as a header.
function TitleField({titleText}) {
    return (
        <h2>
            {titleText}
        </h2>
    );
};

// Return the component body as a list of paragraphs separated by newlines.
function BodyField({bodyText}) {
    return (
        <div>
            {bodyText.split('\n\n').map(paragraph =>
            <p>
                {paragraph.split('\n').reduce((total, line) => [total, <br />, line])}
            </p>
            )}
        </div>
    );
};

// TODO
function BlockNumberField({callback}) {
    return (
        <Form.Group>                                               
            <Form.Label>                                           
                Block Number:                                      
            </Form.Label>                                          
            <Form.Control                                          
                type="number"                                      
                onChange={(event) => {
                    callback(event.target.value);
                    event.preventDefault();
                }}
            />                                                     
        </Form.Group> 
    );
};

// Return the component data field and provide a callback for passing contents
// back to the parent.
function DataField({callback}) {
   return (
        <Form.Group>
            <Form.Label>
                Data:
            </Form.Label>
            <Form.Control
                as="textarea"
                type="text"
                placeholder=""
                onChange={(event) => {
                    callback(event.target.value);
                    event.preventDefault();
                }}
            />
        </Form.Group>
    );
};

// Return the component hash field with passed data hashed using sha256.
function HashField({data}) {
    return (
        <Form.Group>
            <Form.Label>
                Hash:
            </Form.Label>
            <Form.Control
                readOnly
                type="text"
                value={sha256(data)}
            />
        </Form.Group>
    );
};

export { DataField, BodyField, TitleField, HashField, BlockNumberField }
