import React, { useState, useEffect } from 'react';
import { Container, Form } from 'react-bootstrap';
import { NumberField, ReadOnlyTextField, TextAreaField } from '../Fields';
import { MineButton } from '../MineButton';
import MineSettings from '../MineSettings';
import { sha256 } from 'js-sha256';

// Parse the mining settings.
const difficulty = MineSettings.difficulty;
const pattern = MineSettings.pattern;

// Define component globals.                                                      
const GREEN = '#50C878';                                                          
const RED = '#FF0000';

function SingleBlock({block, setBlock}) {
    const {blockNumber, nonce, data, prevHash, hash} = block;
    /*
    const [blockNumber, setBlockNumber] = useState(block.blockNumber);
    const [nonce, setNonce] = useState(block.nonce);
    const [data, setData] = useState(block.data);
    */
    const [color, setColor] = useState(GREEN);
    const [isSigned, setIsSigned] = useState(true);

    // Set the hash.
    useEffect(() => {
        setBlock({...block, hash: String(sha256(blockNumber + nonce + data + prevHash))});
    }, [blockNumber, nonce, data, prevHash]);

    // Set isSigned.
    useEffect(() => {
        setIsSigned(block.hash.substr(0, difficulty) === pattern ? true : false);
    }, [block.hash]);

    // Set color.
    useEffect(() => {
        setColor(isSigned ? GREEN : RED);
    }, [isSigned]);

    // Notify the caller of the changes. 
    /*
    useEffect(() => {
        //callback({...block, blockNumber, nonce, data});
        setBlock({...block, blockNumber, nonce, data});
    }, [blockNumber, nonce, data]);
    */

    return (
        <Container
            style={{
                'width':350,
                'height':500,
                'background-color':color
            }}
        >
            <Form>
                <br/>
                <NumberField
                    label={'Block Number:'}
                    value={blockNumber}
                    callback={(blockNumber) => setBlock({...block, blockNumber})}
                />
                <NumberField
                    label={'Nonce:'}
                    value={nonce}
                    callback={(nonce) => setBlock({...block, nonce})}
                />
                <TextAreaField
                    label={'Data:'}
                    callback={(data) => setBlock({...block, data})}
                />
                <ReadOnlyTextField
                    label={'Previous Hash:'}
                    value={prevHash}
                />
                <ReadOnlyTextField
                    label={'Hash:'}
                    value={hash}
                />
                <br/>
                <MineButton
                    blockNumber={blockNumber}
                    data={data}
                    prevHash={prevHash}
                    callback={(nonce) => setBlock({...block, nonce})}
                />
            </Form>
        </Container>
    );
}

export default SingleBlock;
