import React, { Component } from 'react';
import { Container } from 'react-bootstrap';
import Blockchain from './Blockchain'
import { TitleField, SecondTitleField, BodyField } from '../Fields';
import description from './description';

const title = description.title;
const body = description.body;

const RED = '#CD0000';
const GREEN = '#008000';

class Distributed extends Component {
    constructor(props) {
        super(props);
        this.state = {
            peerA: "",
            peerB: "",
            peerC: "",
        }
    }

    getConsensus() {
        console.log("GET CONSENSUS CALLED");
        this.state.lastHashes.forEach(i => console.log("I: ", i));
    }

    componentDidUpdate(prevProps, prevState) {
        console.log("COMPONENT DID UPDATE");
        this.getConsensus();
    }

    render() {
        return (
            <Container fluid>
                <TitleField titleText={title} />
                <BodyField bodyText={body} />
                <div 
                    style={{
                        'background-color':GREEN
                    }}
                >
                    <SecondTitleField titleText={'Peer A'} /> 
                    <Blockchain peer={0} callback={(hash) => this.setState({peerA: hash})} />
                </div>
                <br />
                <div 
                    style={{
                        'background-color':RED
                    }}
                >
                    <SecondTitleField titleText={'Peer B'} callback={(response) => this.handleUpdate(response)} />
                    <Blockchain peer={1} callback={(hash) => this.setState({peerB: hash})} />
                </div>
                <br />
                <div 
                    style={{
                        'background-color':GREEN
                    }}
                >
                    <SecondTitleField titleText={'Peer C'} callback={(response) => this.handleUpdate(response)} />
                    <Blockchain peer={2} callback={(hash) => this.setState({peerC: hash})} />
                </div>
                <br />
            </Container>
        );
    }
}

export default Distributed;
