import React, {useState} from 'react';
import { Container } from 'react-bootstrap';
import Blockchain from './Blockchain'
import { SecondTitleField } from './Fields';

// Define component globals.                                                      
const GREEN = '#50C878';                                                          
const RED = '#FF0000'; 

function Distributed() {
    return (
        <Container fluid>
            <div style={{'backgroundColor':RED}}>
                <SecondTitleField titleText={'Peer A'} /> 
                <Blockchain peer={0} callback={(response) => this.handleUpdate(response)} />
            </div>
            <div>
                <SecondTitleField titleText={'Peer B'} callback={(response) => this.handleUpdate(response)} />
                <Blockchain peer={1} callback={(response) => this.handleUpdate(response)} />
            </div>
            <div>
                <SecondTitleField titleText={'Peer C'} callback={(response) => this.handleUpdate(response)} />
                <Blockchain peer={2} callback={(response) => this.handleUpdate(response)} />
            </div>
        </Container>
    );
}

export default Distributed;
